import random
from outside_positions_converter import convert_outside_position_to_center
from starting_team_formations import get_positions_from_formation

tiers = [
    ['ST'],
    ['CAM', 'RM', 'LM', 'LW', 'RW', 'CF', 'RF', 'LF'],
    ['CM', 'CDM'],
    ['RB', 'RWB', 'LB', 'LWB', 'CB'],
    ['GK']
]

chances = [
    0.65,
    0.55,
    0.3,
    0.2,
    0
]

def build_rating_chances() :
    current_factor = 1
    rating_chances = {}
    curve = 0.097
    for x in range(1, 100) :
        rating_chances[x] = int(current_factor)
        current_factor = current_factor*(1+curve)
    return rating_chances

rating_chances = build_rating_chances()



def build_striker_dict(lineup, players) :
    return {player_in_lineup:players[player_in_lineup]['Positions']['ST'] for player_in_lineup in lineup}

def build_position_dict(lineup, formation) :
    positions_from_formation = get_positions_from_formation(formation)
    return {lineup[i]:positions_from_formation[i] for i in range(11)}

def get_correct_tier(position) :
    for i, tier in enumerate(tiers) :
        if position in tier :
            return chances[i]

def get_player_randomization_value(striker_rating, position) :
    position = convert_outside_position_to_center(position)
    position_value = get_correct_tier(position)
    striker_value = rating_chances[striker_rating]
    return int(position_value*striker_value)

losing_team_score_dict = {
    0:[40, 80, 95],
    1:[40, 80, 95],
    2:[70, 95, 101],
    3:[80, 95, 101],
    4:[95, 101, 101],
    5:[97, 101, 101],
    6:[101, 101, 101],
    7:[101, 101, 101],
    8:[101, 101, 101],
    9:[101, 101, 101],
    10:[101, 101, 101],
}

def get_losing_team_score(deficit) :
    losing_randomization = random.randint(1, 100)
    for i, value in enumerate(losing_team_score_dict[deficit]) :
        if losing_randomization < value :
            return i
    return len(losing_team_score_dict[deficit])
            
def get_scoring_chances(players, lineup, formation) :
    striker_dict = build_striker_dict(lineup, players)
    position_dict = build_position_dict(lineup, formation)

    player_chances = {}
    total_chances = 0

    for player in lineup :
        striker_rating = striker_dict[player]
        position = position_dict[player]
        player_chances[player] = get_player_randomization_value(striker_rating, position)
        total_chances += player_chances[player]

    for player in player_chances :
        player_chances[player] = int((player_chances[player]/total_chances)*100)
    
    return {player:player_chances[player] for player in player_chances if player_chances[player] != 0}

def randomize_scorer(chances) :
    total_sum = sum([chances[key] for key in chances])
    randos = list(range(1, total_sum+1))
    chances_randos = {player:[] for player in chances}
    for player in chances :
        for x in range(chances[player]) :
            random_index = random.randint(0, len(randos)-1)
            chances_randos[player].append(randos[random_index])
            randos.remove(randos[random_index])
    randomization = random.randint(1, total_sum)
    for player in chances_randos :
        if randomization in chances_randos[player] :
            return player
    return list(sorted(chances_randos, key=lambda x:chances_randos[x]))[1]
        

def randomize_goals(team1, team2, team1_lineup, team2_lineup, team1_name, team2_name, team1_formation, team2_formation, winning_team, difference_in_score) :
    losing_score = get_losing_team_score(difference_in_score)
    winning_score = losing_score+difference_in_score
    team1_chances = get_scoring_chances(team1, team1_lineup, team1_formation)
    team2_chances = get_scoring_chances(team2, team2_lineup, team2_formation)
    if team1_name == winning_team or winning_team == 'Draw' :
        score = '-'.join([str(winning_score), str(losing_score)])
        team1_scorers = [randomize_scorer(team1_chances) for i in range(winning_score)]
        team2_scorers = [randomize_scorer(team2_chances) for i in range(losing_score)]
    else :
        score = '-'.join([str(losing_score), str(winning_score)])
        team1_scorers = [randomize_scorer(team1_chances) for i in range(losing_score)]
        team2_scorers = [randomize_scorer(team2_chances) for i in range(winning_score)]
    return score, team1_scorers, team2_scorers


